example.screen:
options {
        directory "/var/lib/named";
        forwarders { 10.11.12.13; 10.11.12.14; };
        listen-on { 127.0.0.1; &dnsip;; };
        allow-query { 127/8; 192.168/16 };
        notify no;
};

options {
        directory "/var/lib/named";
        forwarders { 10.0.0.1; };
        notify no;
};

zone "localhost" in {
       type master;
       file "localhost.zone";
};

zone "0.0.127.in-addr.arpa" in {
        type master;
        file "127.0.0.zone";
};

zone "." in {
        type hint;
        file "root.hint";
};

zone "example.com" in {
      type master;
      file "example.com.zone";
      notify no;
};

zone "&exampledomain1;" in {
      type slave;
      file "slave/&exampledomain1;.zone";
      masters { 10.0.0.1; }; 
};

1.  $TTL 2D
2.  &exampledomain;. IN SOA      dns  root.&exampledomain;. ( 
3.               2003072441  ; serial
4.               1D          ; refresh
5.               2H          ; retry
6.               1W          ; expiry
7.               2D )        ; minimum
8.
9.               IN NS       dns 
10.              IN MX       10 mail
11.
12. gate         IN A        &gateip; 
13.              IN A        10.0.0.1 
14. dns          IN A        &dnsip; 
15. mail         IN A        &mailip; 
16. &wsI;      IN A        &wsIip;
17. &wsII;        IN A        &wsIIip;
18. &wsIII;       IN A        &wsIIIip;
19. &wsIV;      IN A        &wsIVip;
20. ntp          IN CNAME    dns 
21. dns6         IN A6  0    &dnsipv6;
